import React from "react";

const Form = (props) => {


  const updateSearch = (event) => {
    setSearch(event.target.value);
    console.log(search);
    props.fireQuery()
  };

  return (
    <div>
      <form onSubmit={props.fireQuery} className="search-form">
        <input
          className="search-bar"
          type="text"
          value={search}
          onChange={updateSearch}
        />
        <button className="search-button" type="submit">
          Search
        </button>
      </form>
    </div>
  );
};

export default Form;
